var jsonData = JSON.parse(responseBody);
tests["2 ITService instances ready"] = (jsonData.data.count >=2)
var service_uuids = [];
jsonData.data.results.forEach(function(result){
    service_uuids.push(result.uuid);
})
postman.setEnvironmentVariable("service_uuids", JSON.stringify(service_uuids));

var jsonData = JSON.parse(responseBody);
tests["2 ITServiceGroup instances ready"] = (jsonData.data.length ==2)
var service_group_uuids = [];
jsonData.data.forEach(function(result){
    service_group_uuids.push(result.group.uuid);
})
postman.setEnvironmentVariable("service_group_uuids", JSON.stringify(service_group_uuids));

